import React, { useState } from 'react';
import { useDropzone } from 'react-dropzone';
import './styles/dropzone.css';

const Dropzone = () => {
    const [selectedPhoto, setSelectedPhoto] = useState(null);

    const onDrop = (acceptedFiles) => {
        // Вибір першого прийнятого файлу та збереження його в стані компонента
        const photo = acceptedFiles[0];
        setSelectedPhoto(photo);

        // Якщо ви хочете зберегти файл в локальному сховищі, використовуйте localStorage або sessionStorage
        // localStorage.setItem('photo', photo);
        // sessionStorage.setItem('photo', photo);
    };
    const { getRootProps, getInputProps, isDragActive } = useDropzone({
        onDrop,
        accept: 'image/*', // Дозволяє приймати тільки файли з розширенням .png, .jpg та ін.
    });
    return (
        <div>
            <div {...getRootProps()} className='dropzone'>
                <input {...getInputProps()} />
                <p>{isDragActive ? 'Перетягніть фото сюди' : 'Перетягніть фото сюди або натисніть для вибору'}</p>
            </div>
            {selectedPhoto && (
                <div>
                    <p>Вибрано фото: {selectedPhoto.name}</p>
                    <img src={URL.createObjectURL(selectedPhoto)} alt="Selected" style={{ maxWidth: '100%' }} />
                </div>
            )}
        </div>
    );
};
const MyDropzone = () => {
    const {

        acceptedFiles,
        fileRejections,
        getRootProps,
        getInputProps,
        open,
    } = useDropzone();

    const files = acceptedFiles.map(file => (
        <li key={file.path}>
            {file.path} - {file.size} bytes
        </li>
    ));

    const fileRejectionItems = fileRejections.map(({ file, errors }) => (
        <li key={file.path}>
            {file.path} - {file.size} bytes
            <ul>
                {errors.map(e => (
                    <li key={e.code}>{e.message}</li>
                ))}
            </ul>
        </li>
    ));

    return (
        <section>
            <div {...getRootProps({ className: 'dropzone' })}>
                <input {...getInputProps()} />
                <p>Drag 'n' drop some files here, or click to select files</p>
            </div>
            <aside>
                <h4>Files</h4>
                <ul>{files}</ul>
                {fileRejectionItems.length > 0 && (
                    <>
                        <h4>File Rejections</h4>
                        <ul>{fileRejectionItems}</ul>
                    </>
                )}
            </aside>
        </section>
    );
};

export default MyDropzone;
