import React, { useState } from 'react';
import './styles/logIn.css';
import {Alert, Form} from "react-bootstrap";
import Input from "../components/Input";
import { ButtonV2 } from "../components/Button";

const LogIn = () => {
    const onClick = () => {
        return(
            <Alert variant="danger">
                <Alert.Heading>Oh snap! You got an error!</Alert.Heading>
                <p>
                    Change this and that and try again. Duis mollis, est non commodo
                    luctus, nisi erat porttitor ligula, eget lacinia odio sem nec elit.
                    Cras mattis consectetur purus sit amet fermentum.
                </p>
            </Alert>

        )
        alert('ERRRROR');
        // Тут можна виконати вашу логіку
    };

    const [showPassword, setShowPassword] = useState(false);
    const toggleShowPassword = () => {
        setShowPassword(!showPassword);
    };
    return(
        // <body className='LogIn bg-body'>
        <div className="LogIn">
            <div className='Form bg-light50'>
                <h3 className='Card-title tx-green Caption'><b>Увійти</b></h3>
                <div className='Input-group'>
                    <div className='Inp1'>
                        <Input type='email' placeholder='Пошта'/>
                    </div>
                    <div className='Inp2'>
                        <Input type={showPassword ? 'text' : 'password'} placeholder='Пароль'/>
                        <Form>
                            <Form.Check className='checkbox' type='checkbox' >
                                <Form.Check.Input  type='checkbox' isValid onChange={toggleShowPassword}/>
                                <Form.Check.Label className='Small'>Показати пароль</Form.Check.Label>
                            </Form.Check>
                        </Form>
                    </div>
                </div>
                <div className='button-container'>
                    <ButtonV2 onClick={onClick} title='Продовжити' width={240}/>
                    <a className='link tx-green Button-text-link-2' href='/'>немає акаунту</a>
                </div>
            </div>
        </div>
        // </body>
    );
}
export default LogIn;
