import React from "react";
import './styles/dropdown.css';
import Dropdown from 'react-bootstrap/Dropdown';

const MyDropdown = ({width}) => {
    const CustomToggle = React.forwardRef(({ children, onClick }, ref) => (
        <a
            href=""
            ref={ref}
            onClick={(e) => {
                e.preventDefault();
                onClick(e);
            }}
        >
            {children}
            &#x25bc;
        </a>
    ));

// forwardRef again here!
// Dropdown needs access to the DOM of the Menu to measure it
    const CustomMenu = React.forwardRef(
        ({ children, style, className, 'aria-labelledby': labeledBy }, ref) => {
            const [value, setValue] = useState('');

            return (
                <div
                    ref={ref}
                    style={style}
                    className={className}
                    aria-labelledby={labeledBy}
                >
                    <Form.Control
                        autoFocus
                        className="mx-3 my-2 w-auto"
                        placeholder="Type to filter..."
                        onChange={(e) => setValue(e.target.value)}
                        value={value}
                    />
                    <ul className="list-unstyled">
                        {React.Children.toArray(children).filter(
                            (child) =>
                                !value || child.props.children.toLowerCase().startsWith(value),
                        )}
                    </ul>
                </div>
            );
        },
    );

    render(
        <Dropdown>
            <Dropdown.Toggle as={CustomToggle} id="dropdown-custom-components">
                Custom toggle
            </Dropdown.Toggle>

            <Dropdown.Menu as={CustomMenu}>
                <Dropdown.Item eventKey="1">Red</Dropdown.Item>
                <Dropdown.Item eventKey="2">Blue</Dropdown.Item>
                <Dropdown.Item eventKey="3" active>
                    Orange
                </Dropdown.Item>
                <Dropdown.Item eventKey="1">Red-Orange</Dropdown.Item>
            </Dropdown.Menu>
        </Dropdown>,
    );

    // const Style  = {
    //     width: width || 'auto',
    // };
    // return(
    //     <div className='Dropdown-block' style={Style}>
    //         {/*<Dropdown>*/}
    //         {/*    <Dropdown.Toggle as={CustomToggle} id="dropdown-custom-components">*/}
    //         {/*        Custom toggle*/}
    //         {/*    </Dropdown.Toggle>*/}
    //
    //         {/*    <Dropdown.Menu as={CustomMenu}>*/}
    //         {/*        <Dropdown.Item eventKey="1">Red</Dropdown.Item>*/}
    //         {/*        <Dropdown.Item eventKey="2">Blue</Dropdown.Item>*/}
    //         {/*        <Dropdown.Item eventKey="3" active>*/}
    //         {/*            Orange*/}
    //         {/*        </Dropdown.Item>*/}
    //         {/*        <Dropdown.Item eventKey="1">Red-Orange</Dropdown.Item>*/}
    //         {/*    </Dropdown.Menu>*/}
    //         {/*</Dropdown>,*/}
    //
    //         {/*<Nav>*/}
    //         {/*    <NavDropdown*/}
    //
    //         {/*        id="nav-dropdown-dark-example"*/}
    //         {/*        title="Вибеіть лікаря"*/}
    //         {/*        className="Body2 Dropdown-block-title"*/}
    //
    //         {/*        // id="dropdown-button-dark-example2"*/}
    //         {/*        // variant="secondary"*/}
    //         {/*        // title="Dropdown button"*/}
    //         {/*        // data-bs-theme="dark"*/}
    //         {/*    >*/}
    //         {/*        <NavDropdown.Item href="#action/3.1">І.П. Прізвище1</NavDropdown.Item>*/}
    //         {/*        <NavDropdown.Item href="#action/3.2">І.П. Прізвище2</NavDropdown.Item>*/}
    //         {/*        <NavDropdown.Item href="#action/3.3">І.П. Прізвище3</NavDropdown.Item>*/}
    //         {/*        <NavDropdown.Item href="#action/3.4">І.П. Прізвище4</NavDropdown.Item>*/}
    //         {/*    </NavDropdown>*/}
    //         {/*</Nav>*/}
    //     </div>
    // )
}
export default MyDropdown;
