import React, { useState } from 'react';
import './styles/logIn.css';
// import {Card} from "react-bootstrap";
import Input from "../components/Input";
import {Form} from "react-bootstrap";

const LogIn = () => {
    const [showPassword, setShowPassword] = useState(false);

    const toggleShowPassword = () => {
        setShowPassword(!showPassword);
    };
    return(
        <div className="LogIn">
            <div className='Form'>
                <h3 className='Card-title Caption'><b>Увійти</b></h3>
                <div className='Input-group' >
                    <div className='Inp1'>
                        <Input type='email' placeholder='Пошта'/>
                    </div>
                    <div className='Inp2'>
                        <Input type={showPassword ? 'text' : 'password'} placeholder='Пароль'/>
                        {/*<label className='checkbox-label Small'>*/}
                        {/*    <input className='checkbox' type="checkbox"*/}
                        {/*        checked={showPassword}*/}
                        {/*        onChange={toggleShowPassword}*/}
                        {/*    />*/}
                        {/*    Показати пароль*/}
                        {/*</label>*/}
                        <Form>
                            {['checkbox', 'radio'].map((type) => (
                                <div key={type} className="ddddd">
                                    <Form.Check type={type} id={`sdfsfcheck-api-${type}`}>
                                        <Form.Check.Input type={type} isValid />
                                        {/*<Form.Check.Label>{`Custom api ${type}`}</Form.Check.Label>*/}
                                        {/*<Form.Control.Feedback type="valid">*/}
                                        {/*    You did it!*/}
                                        {/*</Form.Control.Feedback>*/}
                                    </Form.Check>
                                </div>
                            ))}
                        </Form>
                        <Form.Check className='checkbox Small' type='checkbox' label='Показати пароль' onChange={toggleShowPassword}/>
                    </div>
                </div>

                {/*<Card className='Form-card'>*/}
                {/*    <Card.Title className='Card-title Caption'>*/}
                {/*        <b>Увійти</b>*/}
                {/*    </Card.Title>*/}
                {/*    <Card.Body>*/}
                {/*        <Input type='email' placeholder='Пошта'/>*/}
                {/*        <Input type='password' placeholder='Пароль'/>*/}
                {/*    </Card.Body>*/}
                {/*</Card>*/}
            </div>
        </div>
    );
}
export default LogIn;
