import { useState } from 'react';

const useSelectedItem = () => {
    const [selectedItem, setSelectedItem] = useState(null);
    const handleItemClick = (item) => {
        setSelectedItem(item);
    };
    return {
        selectedItem,
        handleItemClick,
    };

}
export default useSelectedItem;
